macro.command:
// Criar e reproduzir sequência com áudio e efeitos visuais
new Sequence()
    // Efeito visual
    .effect()
    .file("jb2a.thunderwave.center.blue") // Caminho do efeito visual
    .atLocation(token) // Localização do token
    .scale(0.5) // Ajuste o tamanho do efeito visual
    .fadeIn(300)
    .fadeOut(300)
    .duration(1043)

    // Efeito de áudio
    .sound()
    .file("modules/dnd5e-animations/assets/sounds/Damage/Explosion/explosion-echo-1.mp3") // Caminho do arquivo de áudio
    .volume(0.5) // Volume do som
    .playIf(true) // Certifica que o som será reproduzido
    .waitUntilFinished() // Aguarda o término do som antes de prosseguir
    .play(); // Inicia a sequência
// Criar mensagem no chat
await ChatMessage.create({
    speaker: ChatMessage.getSpeaker({ token }),
    content: `<strong>Thunderclap lançado!</strong> Uma explosão sonora poderosa emanou de ${token.name}.`
});
